const sliders=document.querySelectorAll(".slider");for(let e=0;e<sliders.length;e++){let t=sliders[e],l=t.querySelectorAll(".slider-control"),a=t.querySelectorAll(".slider-item"),d=`slider-${e}`;addIdToSlider(t,d);for(let e=0;e<l.length;e++){let r=l[e];addSliderIdToControl(r,d),addEventListenerToControl(t,r,a)}}function addIdToSlider(e,t){e.id=t}function addSliderIdToControl(e,t){e.href=`#${t}`}function addEventListenerToControl(e,t,l){t.addEventListener("click",a=>{let d=e.getAttribute("data-active-slide");t.className.includes("next")?d<l.length?(updateActiveSlideData(e,++d),l.forEach(e=>{e.style.transform=`translateX(-${100*(d-1)}%)`})):(updateActiveSlideData(e,d=1),l.forEach(e=>{e.style.transform="translateX(0%)"})):t.className.includes("prev")&&(d>1?(updateActiveSlideData(e,--d),l.forEach(e=>{e.style.transform=`translateX(-${100*(d-1)}%)`})):(d=l.length,updateActiveSlideData(e,d),l.forEach(e=>{e.style.transform=`translateX(-${100*(l.length-1)}%)`})))})}function updateActiveSlideData(e,t){e.setAttribute("data-active-slide",t)}
